import React from "react";
import { Box, Toast } from "gestalt";

const ToastMessage = ({ show, message }) =>
  show && (
    <Box
      dangerouslySetInlineStyle={{
        __style: {
          bottom: 250,
          left: "50%",
          transform: "translateX(-50%)"
        }
      }}
      position="fixed"
    >
      <Toast  dangerouslySetInlineStyle={{
        __style: {
          color:'#000000'
        }
      }} text={message} />
    </Box>
  );

export default ToastMessage;
